    body: {
      fill: '#efdbff',
      stroke: '#9254de',
      strokeWidth: 2,
    },
  },
})

graph.addNode({
  shape: 'rect',
  width: 120,
  height: 50,
  x: 500,
  y: 250,
  label: 'World',
  attrs: {
    body: {
      fill: '#ffd591',
      stroke: '#ffa940',
      strokeWidth: 2,
    },
  },
})

graph.bindKey('meta', () => {
  source.attr('body/magnet', true)
})

graph.on('edge:connected', () => {
  source.attr('body/magnet', false)
})

