              fill: '#fff',
              stroke: '#31d0c6',
              strokeWidth: 2,
              r: 8
            },
            text: {
              fill: '#666',
              fontSize: 12
            }
          }
        }
      }
    }
  });
  Array.from({
    length: 3
  }).forEach(function (_, index) {
    var label = index === 2 ? {
      position: {
        args: {
          x: 20,
          y: -20
        }
      }
    } : {};
    var stroke = index === 2 ? {
      stroke: 'red'
    } : {};
    var fill = index === 2 ? {
      fill: 'red'
    } : {};
    rect.addPort({
      label: label,
      group: 'a',
      attrs: {
        circle: {
          magnet: true,
          ...stroke
        },
        text: {
          text: "P".concat(index),
          ...fill
        }
      }
    });
  });
  var sides = ['left', 'top', 'right', 'bottom'];
  var index = 0;

  function run() {
    index += 1;

    if (index === sides.length) {
      index = 0;
    }

    rect.prop('ports/groups/a/label/position/name', sides[index]);
    rect.attr('label/text', sides[index]);
    setTimeout(run, 1000);
  }

  run();
});

