        var _fix = dir === 'TB' ? sourceBBox.height : 0;

        var y = sourceBBox.y + _fix + _gap / 2;
        edge.setVertices([{
          x: sourceBBox.center.x,
          y: y
        }, {
          x: targetBBox.center.x,
          y: y
        }]);
      } else {
        edge.setVertices([]);
      }
    });
    graph.unfreeze();
  }

  function createNode(rank, name, image, background) {
    var textColor = arguments.length > 4 && arguments[4] !== undefined ? arguments[4] : '#000';
    return graph.createNode({
      shape: 'org-node',
      attrs: {
        '.card': {
          fill: background
        },
        '.image': {
          xlinkHref: image
        },
        '.rank': {
          fill: textColor,
          text: _x.Dom.breakText(rank, {
            width: 160,
            height: 45
          })
        },
        '.name': {
          fill: textColor,
          text: _x.Dom.breakText(name, {
            width: 160,
            height: 45
          })
        },
        '.btn > circle': {
          stroke: textColor
        },
        '.btn > text': {
          fill: textColor,
          stroke: textColor
        }
      }
    });
  }

  function createEdge(source, target) {
    return graph.createEdge({
      shape: 'org-edge',
      source: {
        cell: source.id
      },
      target: {
        cell: target.id
      }
    });
  }

  var nodes = [createNode('Founder & Chairman', 'Pierre Omidyar', male, '#31d0c6'), createNode('President & CEO', 'Margaret C. Whitman', female, '#31d0c6'), createNode('President, PayPal', 'Scott Thompson', male, '#7c68fc'), createNode('President, Ebay Global Marketplaces', 'Devin Wenig', male, '#7c68fc'), createNode('Senior Vice President Human Resources', 'Jeffrey S. Skoll', male, '#fe854f'), createNode('Senior Vice President Controller', 'Steven P. Westly', male, '#feb663')];
  var edges = [createEdge(nodes[0], nodes[1]), createEdge(nodes[1], nodes[2]), createEdge(nodes[1], nodes[3]), createEdge(nodes[1], nodes[4]), createEdge(nodes[1], nodes[5])];
  graph.resetCells([].concat(nodes, edges));
  layout();
  graph.zoomTo(0.8);
  graph.centerContent();
  setup();
});

